  {%- for post in posts.toArray() %}

    {{ post.title | noControlChars | striptags | cdata | safe }}
    {{ (config.root + post.path) | urlencode }}
    {% if content %}
      {{ post[raw] | default(post.content) | replace('.*?', "", "g") | striptags | cdata | safe }}
    {% endif %}
    {%- if post.categories and post.categories.length > 0 %}

        {%- for cate in post.categories.toArray() %}
        {{ cate.name }}
        {%- endfor %}

    {%- endif %}
    {%- if post.tags and post.tags.length > 0 %}

        {%- for tag in post.tags.toArray() %}
        {{ tag.name }}
        {%- endfor %}

    {%- endif %}

  {%- endfor %}
{%- endif %}
