  {%- for page in pages.toArray() %}

    {{ page.title | noControlChars | striptags | cdata }}>
    {{ (config.root + page.path) | urlencode }}
    {% if content %}
      {{ page[raw] | default(page.content) | replace('.*?', "", "g") | striptags | cdata }}
    {% endif %}

